import React, { useState } from 'react';

const IncomeModal = ({ isOpen, closeModal, selectedDate }) => {
  const [incomeSource, setIncomeSource] = useState('');
  const [incomeAmount, setIncomeAmount] = useState('');

  const handleIncomeSourceChange = (e) => {
    setIncomeSource(e.target.value);
  };

  const handleIncomeAmountChange = (e) => {
    setIncomeAmount(e.target.value);
  };

  const handleRegister = () => {

    if (incomeSource.trim() === '' || incomeAmount.trim() === '') {
      alert('수입 출처와 수입 금액을 모두 입력해주세요.');
      return;
    }




    console.log('Income Amount:', incomeAmount);


    closeModal();
  };

  return (
    <div className={`income-modal ${isOpen ? 'open' : ''}`}>
      <div className="date">
        {selectedDate.toLocaleDateString()}
      </div>
      <p> [부가 수입] </p>

      <div className="Income-content">
        <p> 수입 출처 : </p>
        <input
          className='text'
          type='text'
          placeholder="수입 출처를 입력해주세요"
          value={incomeSource}
          onChange={handleIncomeSourceChange}
        />
        <p> 수입 금액 : </p>
        <input
          className='num'
          type='number'
          placeholder="수입 금액을 입력해주세요"
          value={incomeAmount}
          onChange={handleIncomeAmountChange}
        />
      </div>
      <p>
        <button className="btnOk" onClick={handleRegister}>
          등록
        </button>
        &nbsp;
        <button className="btnCancel2" onClick={closeModal}>
          취소
        </button>
      </p>
    </div>
  );
};

export default IncomeModal;
