import React, { useState, useEffect } from 'react';
import { Bar } from 'react-chartjs-2';
import { Chart, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from 'chart.js/auto';

Chart.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const options = {
  responsive: true,
  plugins: {
    legend: {
      position: 'top',
    },
    title: {
      display: true,
      text: '월별 수입 및 지출 분석',
    },
  },
  scales: {
    y: {
      beginAtZero: true,
      stepSize: 0.1, // Y 축 칸당 0.1원으로 설정
      ticks: {
        callback: function (value, index, values) {
          return value.toFixed(1) + '원';
        },
      },
    },
  },
};

const labels = ['1월', '2월', '3월', '4월', '5월', '6월', '7월', '8월', '9월', '10월', '11월', '12월'];

export const Analyze = ({ updateMonthlyData }) => {
    const [monthlyData, setMonthlyData] = useState(0); // 0 대신 초기값으로 사용할 다른 값으로 변경

    useEffect(() => {
      console.log("Fetching monthly data...");

      // 월별 데이터 가져오기
      const fetchedData = Array(12).fill(0).map((_, index) => {
        const storedData = JSON.parse(localStorage.getItem(`monthlyData_${index}`)) || 0;
        return storedData;
      });

      console.log("Fetched data:", fetchedData);
      setMonthlyData(fetchedData.reduce((accumulator, currentValue) => accumulator + currentValue, 0));
    }, [updateMonthlyData]);


  const data = {
    labels,
    datasets: [
      {
        label: '월별 근무',
        data: monthlyData,
        backgroundColor: 'rgb(0, 0, 255)',
      },
    ],
  };

  return (
    <div className="analyze">
      <Bar options={options} data={data} />
    </div>
  );
};
