import React, { useState, useEffect } from "react";
import { NavLink } from "react-router-dom";
import "./Navbar.css";

export const Navbar = () => {
  const [isHovered, setIsHovered] = useState(false); 
  const getCurrentMonthKey = () => {
    const currentDate = new Date();
    const year = currentDate.getFullYear();
    const month = currentDate.getMonth() + 1;
    return `${year}-${month}`;
  };

  const getMonthDays = (year, month) => {
    return new Date(year, month, 0).getDate();
  };

  const calculateMonthlySum = (monthKey) => {
    const daysInMonth = getMonthDays(new Date().getFullYear(), new Date().getMonth() + 1);
    let sum = 0;

    for (let day = 1; day <= daysInMonth; day++) {
      const currentDateKey = `${monthKey}-${day}`;
      const savedValue = localStorage.getItem(currentDateKey) || '0';
      sum += parseInt(savedValue, 10);
    }

    return sum.toString();
  };

  const [currentAsset, setCurrentAsset] = useState(() => {
    const savedValue = calculateMonthlySum(getCurrentMonthKey());
    return savedValue || '0';
  });

  useEffect(() => {
    const intervalId = setInterval(() => {
      const savedValue = calculateMonthlySum(getCurrentMonthKey());
      setCurrentAsset(savedValue || '0');
    }, 1000);

    return () => clearInterval(intervalId);
  }, []);

  return (
    <nav role="navigation">
      <div id="menuToggle"
        onMouseEnter={() => setIsHovered(true)} // 마우스가 MyPage 메뉴에 들어왔을 때
        onMouseLeave={() => setIsHovered(false)} // 마우스가 MyPage 메뉴에서 나갔을 때
      >
        <input type="checkbox" />
        <span></span>
        <span></span>
        <span></span>


        <ul id="menu">
          <li className="asset"> <b>현재 자산</b>
          <br/>&nbsp;{currentAsset}원</li>
          <li>
            <NavLink to="/calendar">Calendar</NavLink>
          </li>
          <li>
            <NavLink to="/analyze">Analyze</NavLink>
          </li>
          <li>
            <NavLink to="/mypage">Mypa</NavLink>
          </li>
        </ul>
      </div>
    </nav>
  );
};
