import React, { useState, useEffect } from "react";
import { NavLink } from "react-router-dom";
import "./Navbar.css";

export const Navbar = () => {
  const getCurrentMonthWorkKey = () => {
    const currentDate = new Date();
    const year = currentDate.getFullYear();
    const month = currentDate.getMonth() + 1;
    return `work ${year}-${month}`;
  };

  const getCurrentMonthExpenditureKey = () => {
    const currentDate = new Date();
    const year = currentDate.getFullYear();
    const month = currentDate.getMonth() + 1;
    return `expenditure ${year}-${month}`;
  };

  const getMonthDays = (year, month) => {
    return new Date(year, month, 0).getDate();
  };

  const calculateMonthlySum = (monthKey, isExpenditure = false) => {
    const daysInMonth = getMonthDays(new Date().getFullYear(), new Date().getMonth() + 1);
    let sum = 0;

    for (let day = 1; day <= daysInMonth; day++) {
      const currentDateKey = `${monthKey}-${day}`;
      const savedValue = localStorage.getItem(currentDateKey) || '0';
      const value = parseInt(savedValue, 10);

      if (isExpenditure) {
        sum -= value; // 음수 값이면 그대로 빼기
      } else {
        sum += Math.max(0, value); // 양수 값만 더하기
      }
    }

    return sum.toString();
  };

  const [currentAsset, setCurrentAsset] = useState(() => {
    const workKey = getCurrentMonthWorkKey();
    const expenditureKey = getCurrentMonthExpenditureKey();
    const workValue = calculateMonthlySum(workKey);
    const expenditureValue = calculateMonthlySum(expenditureKey, true);
    const savedValue = workValue - expenditureValue;
    return savedValue.toString() || '0';
  });

  useEffect(() => {
    const intervalId = setInterval(() => {
      const workKey = getCurrentMonthWorkKey();
      const expenditureKey = getCurrentMonthExpenditureKey();
      const workValue = calculateMonthlySum(workKey);
      const expenditureValue = calculateMonthlySum(expenditureKey);
      const savedValue = workValue - expenditureValue;
      setCurrentAsset(savedValue.toString() || '0');
    }, 1000);

    return () => clearInterval(intervalId);
  }, []);

  const [isMypageActive, setIsMypageActive] = useState(false);

  const handleMypageClick = () => {
    setIsMypageActive(!isMypageActive);
  };

  return (
    <nav role="navigation">
      <div id="menuToggle">
        <input type="checkbox" />
        <span></span>
        <span></span>
        <span></span>

        <ul id="menu">
          <li className="asset">
            <b>현재 자산</b>
            <br/>&nbsp;{currentAsset}원
          </li>
          <li>
            <NavLink to="/calendar">
              Calendar
            </NavLink>
          </li>

          <li>
            <NavLink to="/analyze">
              분석
            </NavLink>
          </li>

          <li>
            <NavLink 
              to="/mypage" 
              onClick={handleMypageClick}
            >
              Mypage
            </NavLink>
            {isMypageActive && (
              <ul>
                <li>
                  <NavLink to="/mypageWork">근무</NavLink>
                </li>
                <li>
                  <NavLink to="/mypageExpenditure">지출</NavLink>
                </li>
              </ul>
            )}
          </li>
        </ul>
      </div>
    </nav>
  );
};
