import React, { useState, useEffect } from "react";
import { NavLink } from "react-router-dom";
import "./Navbar.css";

export const Navbar = () => {
  const getCurrentMonthKey = () => {
    const currentDate = new Date();
    const year = currentDate.getFullYear();
    const month = currentDate.getMonth() + 1;
    return `${year}-${month}`;
  };

  const getMonthDays = (year, month) => {
    return new Date(year, month, 0).getDate();
  };

  const calculateMonthlySum = (monthKey) => {
    const daysInMonth = getMonthDays(new Date().getFullYear(), new Date().getMonth() + 1);
    let sum = 0;

    for (let day = 1; day <= daysInMonth; day++) {
      const currentDateKey = `${monthKey}-${day}`;
      const savedValue = localStorage.getItem(currentDateKey) || '0';
      sum += parseInt(savedValue, 10);
    }

    return sum.toString();
  };

  const [currentAsset, setCurrentAsset] = useState(() => {
    const savedValue = calculateMonthlySum(getCurrentMonthKey());
    return savedValue || '0';
  });

  useEffect(() => {
    const intervalId = setInterval(() => {
      const savedValue = calculateMonthlySum(getCurrentMonthKey());
      setCurrentAsset(savedValue || '0');
    }, 1000);

    return () => clearInterval(intervalId);
  }, []);

  const [isMypageHovered, setIsMypageHovered] = useState(false);
  return (
    <nav role="navigation">
      <div id="menuToggle">
        <input type="checkbox" />
        <span></span>
        <span></span>
        <span></span>

        <ul id="menu">
          <li className="asset">
            <b>현재 자산</b>
            <br/>&nbsp;{currentAsset}원
          </li>
          <li>
            <NavLink to="/calendar">Calendar</NavLink>
          </li>

          <li>
            <NavLink to="/analyze">분석</NavLink>
          </li>

          {/* <li className={`mypage ${isMypageHovered ? 'hovered' : ''}`}>
            <NavLink to="/mypage">Mypage</NavLink>
            {isMypageHovered && ( // MyPage 메뉴에 호버했을 때만 서브메뉴 표시
              <ul className="submenu">
                <li>근무</li>
                <li>지출</li>
              </ul>
            )}
          </li> */}

<li>
  <NavLink to="/mypage" onMouseEnter={() => setIsMypageHovered(true)} onMouseLeave={() => setIsMypageHovered(false)}>
    Mypage
  </NavLink>
  {isMypageHovered && (
    <ul className="submenu">
      <li>근무</li>
      <li>지출</li>
    </ul>
  )}
</li>


        </ul>
      </div>
    </nav>
  );
};
